create table agendamento_destinos (id_agendamento int8 generated by default as identity, destino varchar(255), forma_pagamento varchar(255), nome varchar(255), telefone varchar(255), primary key (id_agendamento));
create table cadastro_mensagem_contato (id_msg int8 generated by default as identity, aceita_receber_not boolean, email varchar(255), msg varchar(255), nome_de_contato varchar(255), primary key (id_msg));
